--Project 0 SQL

CREATE TABLE USERS(
    USER_ID NUMBER(5) GENERATED BY DEFAULT ON NULL AS IDENTITY INCREMENT BY 1,
    NAME VARCHAR2(15),
    USERNAME VARCHAR2(12),
    EMAIL VARCHAR2(50),
    PASSWORD VARCHAR2(10)
);

CREATE TABLE ACCOUNTS(
    USER_ID NUMBER(5) GENERATED BY DEFAULT ON NULL AS IDENTITY INCREMENT BY 1,
    --ACCOUNT_ID NUMBER(5) GENERATED BY DEFAULT ON NULL AS IDENTITY INCREMENT BY 1,
    BALANCE NUMBER(10, 2)
);

--Testing some queries and dropping/truncating to easily reset/test DB
DROP TABLE USERS;
DROP TABLE ACCOUNTS;
TRUNCATE TABLE ACCOUNTS;
TRUNCATE TABLE USERS;

SELECT ACCOUNTS.USER_ID, USERS.NAME, ACCOUNTS.BALANCE
FROM USERS
JOIN ACCOUNTS
ON USERS.USER_ID = ACCOUNTS.USER_ID
WHERE USERS.USER_ID = 3;

SELECT *
FROM USERS
JOIN ACCOUNTS
ON USERS.USER_ID = ACCOUNTS.USER_ID;

